// DOM elements
const greeting = document.getElementById('greeting');
const changeColorBtn = document.getElementById('changeColorBtn');
const animateBtn = document.getElementById('animateBtn');

// Color palette for random colors
const colors = ['#ff6b6b', '#4ecdc4', '#45b7d1', '#96ceb4', '#feca57', '#ff9ff3'];

// Change color function
changeColorBtn.addEventListener('click', () => {
    const randomColor = colors[Math.floor(Math.random() * colors.length)];
    greeting.style.color = randomColor;
});

// Animate text function
animateBtn.addEventListener('click', () => {
    greeting.classList.add('animate');

    // Remove animation class after animation completes
    setTimeout(() => {
        greeting.classList.remove('animate');
    }, 600);
});

// Add some interactive effects
greeting.addEventListener('mouseover', () => {
    greeting.style.transform = 'scale(1.05)';
    greeting.style.transition = 'transform 0.3s ease';
});

greeting.addEventListener('mouseout', () => {
    greeting.style.transform = 'scale(1)';
});

// Console greeting
console.log('🚀 Hello World Frontend is running!');
console.log('✨ Welcome to your first frontend project!');
